import React, { useEffect } from 'react';
import ScrollReveal from 'scrollreveal';

function App() {
  useEffect(() => {
    ScrollReveal().reveal('.reveal', {
      distance: '50px',
      duration: 800,
      easing: 'ease-in-out',
      origin: 'bottom',
      interval: 200,
      reset: false,
    });
  }, []);

  return (
    <div style={{ padding: '2rem' }}>
      <h1 className="reveal">React Scroll Reveal Animation</h1>
      <p className="reveal">
        Scroll down to see elements animate into view using ScrollReveal!
      </p>
      <div style={{ height: '100vh' }}></div>
      <h2 className="reveal">Section 1</h2>
      <p className="reveal">
        This section will animate as you scroll to it.
      </p>
      <div style={{ height: '100vh' }}></div>
      <h2 className="reveal">Section 2</h2>
      <p className="reveal">
        Another section with scroll reveal animation.
      </p>
    </div>
  );
}

export default App;
