import React from 'react';
import ScrollReveal from 'scrollreveal';
import './styles/App.css';

const scrollRevealOption = {
    distance: "50px",
    origin: "bottom",
    duration: 1000,
};

const ScrollRevealEffects = () => {
    React.useEffect(() => {
        ScrollReveal().reveal(".container .letter-s", {
            duration: 1000,
            delay: 1000,
        });
        ScrollReveal().reveal(".container img", {
            duration: 1000,
            delay: 1500,
        });
        ScrollReveal().reveal(".container .text__left", {
            ...scrollRevealOption,
            origin: "right",
            delay: 2000,
        });
        ScrollReveal().reveal(".container .text__right", {
            ...scrollRevealOption,
            origin: "left",
            delay: 2000,
        });
        ScrollReveal().reveal(".container .explore", {
            duration: 1000,
            delay: 2500,
        });
        ScrollReveal().reveal(".container .catalog", {
            duration: 1000,
            delay: 5000,
        });
        ScrollReveal().reveal(".container .print", {
            duration: 1000,
            delay: 5500,
        });
        ScrollReveal().reveal(".container .h5", {
            duration: 1000,
            interval: 500,
            delay: 3000,
        });
        ScrollReveal().reveal(".footer p", {
            duration: 1000,
            delay: 7000,
        });
    }, []);

    return (
        <div className="container">
            {/* Add your content here */}
        </div>
    );
};

export default ScrollRevealEffects;
